import React from 'react'
import img from '../images/BeanBag3.jpeg'
import { useState } from 'react';

const cartProducts = [
    {
        description:'Farina half leather sectional sofa' ,
        price:'3000 $',
      },
      {
        description:"Versatile sectional with ample seating",
        price:'1800 $'
      },
      {
        description:"Convertible sofa for flexible living",
        price:'5500 $'
      },
]

export default function Cart() {

    const [selectedValue, setSelectedValue] = useState('');

    const handleChange = (event) => {
        setSelectedValue(event.target.value);
    };

  return (
    <React.Fragment>
        <div className='CartContainer'>
            <div className='CartList'>
                <div className='CartListElem'>
                    <img src={img} alt='image'/>
                    <div>
                        <div className='CL-Description'>
                            <h1>{cartProducts[0].description}</h1>
                            <h1>{cartProducts[0].price}</h1>
                        </div>
                        <div className='Cl-Remove'>
                            <div className='CL-'>
                                <p>Quantity :</p>
                                <select value={selectedValue} onChange={handleChange}>
                                    <option value={1}>1</option>
                                    <option value={2}>2</option>
                                    <option value={3}>3</option>
                                </select>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className='CheckoutInfo'></div>
        </div>
    </React.Fragment>
  )
}
