import React, { useState } from "react";
import axios from "axios";

export default function SignupForm() {
  const [errorMessage, setErrorMessage] = useState([]);
  const [passMatch, setPassMatch] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    username: "",
    password: "",
    confirmPassword: "",
  });

  // const [name,setName] = useState('');
  // const [email,setEmail] = useState('');
  // const [username,setUsername] = useState('');
  // const [password,setPassword] = useState('');

  const changeFormData = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  function checkPassword() {
    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    var currentErrors = [];
    var curPassword = formData.password;
    var uppercase = false;
    var symbol = false;
    var lencheck = false;

    if(formData.password!==formData.confirmPassword)
      {
        setPassMatch("Passwords does'nt match");
      }

    if (curPassword.length >= 8) {
      lencheck = true;
    } else {
      currentErrors.push("Password must contain at least 8 characters");
    }
    if (/[A-Z]/.test(curPassword)) {
      uppercase = true;
    } else {
      currentErrors.push(
        "Password must contain at least 1 uppercase character"
      );
    }
    if (specialChars.test(curPassword)) {
      symbol = true;
    } else {
      currentErrors.push("Password must contain at least 1 special character");
    }
    setErrorMessage(currentErrors);
    return uppercase && symbol && lencheck ;
  }

  const formSubmit = (e) => {
    e.preventDefault();
    // axios.post("http://localhost:5000/signup", formData);
    var isok = checkPassword();
    if (isok) {
      console.log("Form submitted successfully");
    } else {
      console.log("Something wrong in the form");
    }
  };

  return (
    <React.Fragment>
      <div className="formcontainer">
        <h2 className="signupHeading">Create an Account</h2>
        <div className="formDiv">
          <form className="signupForm" method="post" onSubmit={formSubmit}>
            <div className="formInputDiv">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={changeFormData}
                required
              />
            </div>
            <div className="formInputDiv">
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={changeFormData}
                required
              />
            </div>
            <div className="formInputDiv">
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={changeFormData}
                required
              />
            </div>

            <div className="formInputDiv">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={changeFormData}
                required
              />
              <div className="errorField">
                
              <ul>
                {errorMessage.map((msg) => (
                    <li>{msg}</li>
                ))}
                </ul>
              </div>
            </div>
            <div className="formInputDiv">
              <label htmlFor="confirmPassword">Confirm Password:</label>
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm Password"
                value={formData.confirmPassword}
                onChange={changeFormData}
                required
              />
              <div className="errorField">
                <ul>
                  <li>{passMatch}</li>
                </ul>
              </div>
            </div>

              <input type="submit" value="Submit" />
            
          </form>
        </div>
      </div>
      <div style={{textAlign:'center'}}><p>Already have an account ? <a href="#" style={{}}>Sign In here</a></p></div>
    </React.Fragment>
  );
}
